import React from 'react';
import { Grid, Typography } from '@mui/material';
import { Link } from 'react-router-dom'; // Importamos Link para navegación
import data from '../data.json';
import NewsCard from '../componentes/NewsCard';
import MatchCard from '../componentes/MatchCard';
import 'flag-icons/css/flag-icons.min.css'; // Importamos los estilos de las banderas

const Home = () => {
  const { news, upcomingMatches, sportsResults } = data;

  return (
    <Grid container spacing={2}>
      <Grid item xs={12}>
        <Typography variant="h4" gutterBottom>Noticias destacadas</Typography>
        <Grid container spacing={2}>
          {news.map(article => (
            <Grid item xs={12} sm={6} md={4} key={article.id}>
              <NewsCard article={article} />
            </Grid>
          ))}
        </Grid>
      </Grid>

      <Grid item xs={12}>
        <Typography variant="h4" gutterBottom>Resultados de la Copa América</Typography>
        <Grid container spacing={2}>
          {upcomingMatches.map(match => (
            <Grid item xs={12} sm={6} md={4} key={match.id}>
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <span className={`fi fi-${match.team1FlagCode} fs-large`} style={{ marginRight: '10px' }}></span>
                <Link to={`/match/${match.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                  <MatchCard match={match} />
                </Link>
                <span className={`fi fi-${match.team2FlagCode} fs-large`} style={{ marginLeft: '10px' }}></span>
              </div>
            </Grid>
          ))}
        </Grid>
      </Grid>

      <Grid item xs={12}>
        <Typography variant="h4" gutterBottom>Noticias</Typography>
        <Grid container spacing={2}>
          {sportsResults.map((result, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <div>
                <a href={result.link} target="_blank" rel="noopener noreferrer">
                  <Typography variant="h6">{result.title}</Typography>
                </a>
                <Typography variant="body2">{result.snippet}</Typography>
              </div>
            </Grid>
          ))}
        </Grid>
      </Grid>
    </Grid>
  );
};

export default Home;
