import React from 'react';
import { useParams } from 'react-router-dom';
import data from '../data.json';
import { jugadoress } from '../jugadores';
import '../componentes/estilos/Match.css';

const titularesArgentina = [
  { "POS": 23, "Nombre": "Emiliano Martínez" },
  { "POS": 4, "Nombre": "Gonzalo Montiel" },
  { "POS": 13, "Nombre": "Cristian Romero" },
  { "POS": 25, "Nombre": "Lisandro Martínez" },
  { "POS": 3, "Nombre": "Nicolás Tagliafico" },
  { "POS": 7, "Nombre": "Rodrigo De Paul" },
  { "POS": 24, "Nombre": "Enzo Fernández" },
  { "POS": 20, "Nombre": "Alexis Mac Allister" },
  { "POS": 11, "Nombre": "Ángel Di María" },
  { "POS": 10, "Nombre": "Lionel Messi (C)" },
  { "POS": 9, "Nombre": "Julián Álvarez" }
];

const Match = () => {
  const { matchId } = useParams();
  const match = data.upcomingMatches.find(m => m.id === parseInt(matchId));

  if (!match) {
    return <div>Partido no encontrado</div>;
  }

  const team1Players = jugadoress.filter(player => titularesArgentina.some(titular => titular.POS === player.POS));

  return (
    <div>
      <h2>Formación del Partido: {match.team1} vs {match.team2}</h2>
      <div className="field">
        {team1Players.map(player => (
          <div className="player" style={getPlayerStyle(player.POS)} key={player.POS}>
            <div className="player-number">{player.POS}</div>
            <div className="tooltip">{player.Nombre}</div>
          </div>
        ))}
        {/* Aquí puedes agregar jugadores de Colombia de manera similar */}
      </div>
    </div>
  );
};

// Función para obtener los estilos de posición de los jugadores
const getPlayerStyle = (pos) => {
  const positions = {
    23: { top: '38%', left: '2.5%' }, // Posición de Emiliano Martínez
    4: { top: '63%', left: '5%' },  // Posición de Gonzalo Montiel
    13: { top: '50%', left: '5.5%' }, // Posición de Cristian Romero
    25: { top: '28%', left: '6.5%' }, // Posición de Lisandro Martínez
    3: { top: '17%', left: '6.5%' },  // Posición de Nicolás Tagliafico
    7: { top: '62%', left: '13%' },  // Posición de Rodrigo De Paul
    24: { top: '49%', left: '13%' }, // Posición de Enzo Fernández
    20: { top: '37%', left: '12%' }, // Posición de Alexis Mac Allister
    11: { top: '20%', left: '14%' }, // Posición de Ángel Di María
    10: { top: '10%', right: '10%' }, // Posición de Lionel Messi
    9: { top: '20%', right: '20%' }, // Posición de Julián Álvarez
    // Agrega más posiciones según sea necesario
  };
  return positions[pos] || {};
};

export default Match;
