import React, { useState, useEffect } from 'react';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import TextField from '@mui/material/TextField';
import { useNavigate } from 'react-router-dom';
import './estilos/Header.css';

const Header = () => {
  const [anchorEl, setAnchorEl] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    console.log("Renderizando Header..."); // Log para depuración
  }, []);

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  return (
    <AppBar position="static" className="app-bar">
      <Toolbar>
        <Typography variant="h6" className="logo">
          <img src="logo.png" alt="Logo" height="30" />
          Mi Portal Deportivo
        </Typography>

        <div className="navbar-menu">
          <TextField
            placeholder="Buscar..."
            //variant="outlined"
            size="small"
            style={{ marginRight: '20px', backgroundColor: 'grey', borderRadius: '8px' }}
          />
          
          <IconButton edge="end" color="inherit" aria-label="menu" onClick={handleMenuOpen}>
            <MenuIcon />
          </IconButton>
          <Menu
            id="simple-menu"
            anchorEl={anchorEl}
            keepMounted
            open={Boolean(anchorEl)}
            onClose={handleMenuClose}
          >
            <MenuItem onClick={() => { navigate('/home'); handleMenuClose(); }}>Home</MenuItem>
            <MenuItem onClick={() => { navigate('/players'); handleMenuClose(); }}>Jugadores</MenuItem>
            <MenuItem onClick={() => { navigate('/teams'); handleMenuClose(); }}>Equipos</MenuItem>
            <MenuItem onClick={() => { navigate('/results'); handleMenuClose(); }}>Resultados</MenuItem>
            <MenuItem onClick={() => { navigate('/statistics'); handleMenuClose(); }}>Estadísticas</MenuItem>
            <MenuItem onClick={() => { navigate('/register'); handleMenuClose(); }}>Registro</MenuItem>
            <MenuItem onClick={() => { navigate('/login'); handleMenuClose(); }}>Iniciar sesión</MenuItem>
          </Menu>
        </div>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
