import React, { useState, useEffect } from 'react';
import Grid2 from '@mui/material/Grid2'; // Ruta correcta para importar Grid2
import { Typography } from '@mui/material';
import axios from 'axios'; 
import NewsCard from '../componentes/NewsCard';
import MatchCard from '../componentes/MatchCard';

const Home = () => {
  const [news, setNews] = useState([]);
  const [upcomingMatches, setUpcomingMatches] = useState([]);
  const [sportsResults, setSportsResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:3001/api/news')
      .then(response => setNews(response.data))
      .catch(error => console.error('Error al obtener las noticias:', error));

    axios.get('http://localhost:3001/api/matches')
      .then(response => setUpcomingMatches(response.data))
      .catch(error => console.error('Error al obtener los partidos:', error));
  }, []);

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/sports/results?q=resultados+copa+america');
        setSportsResults(response.data);
        setLoading(false);
      } catch (error) {
        setError('Error al obtener los resultados deportivos');
        setLoading(false);
      }
    };
    fetchResults();
  }, []);

  return (
    <Grid2 container spacing={2}>
      <Grid2 item xs={12}>
        <Typography variant="h4" gutterBottom>Noticias destacadas</Typography>
        <Grid2 container spacing={2}>
          {news.map(article => (
            <Grid2 item xs={12} sm={6} md={4} key={article.id}>
              <NewsCard article={article} />
            </Grid2>
          ))}
        </Grid2>
      </Grid2>
      
      <Grid2 item xs={12}>
        <Typography variant="h4" gutterBottom>Próximos partidos</Typography>
        <Grid2 container spacing={2}>
          {upcomingMatches.map(match => (
            <Grid2 item xs={12} sm={6} md={4} key={match.id}>
              <MatchCard match={match} />
            </Grid2>
          ))}
        </Grid2>
      </Grid2>
      
      <Grid2 item xs={12}>
        <Typography variant="h4" gutterBottom>Resultados de la Copa América</Typography>
        {loading ? (
          <Typography>Cargando resultados...</Typography>
        ) : error ? (
          <Typography color="error">{error}</Typography>
        ) : (
          <Grid2 container spacing={2}>
            {sportsResults.map((result, index) => (
              <Grid2 item xs={12} sm={6} md={4} key={index}>
                <div>
                  <a href={result.link} target="_blank" rel="noopener noreferrer">
                    <Typography variant="h6">{result.title}</Typography>
                  </a>
                  <Typography variant="body2">{result.snippet}</Typography>
                </div>
              </Grid2>
            ))}
          </Grid2>
        )}
      </Grid2>
    </Grid2>
  );
};

export default Home;
