import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { jugadoresUruguay } from '../jugadoresUruguay';
import { jugadoresBrasil } from '../jugadoresBrasil';
import { Box, Typography, Grid } from '@mui/material';
import PlayerCard from '../componentes/PlayerCard';

const Match2 = () => {
  const { matchId } = useParams();
  const [match, setMatch] = useState(null);

  useEffect(() => {
    // Simulación de la carga de datos de un archivo JSON
    // Deberías reemplazar esto con la carga real de datos de tu fuente
    const matches = [
      {
        id: '2',
        team1: 'Uruguay',
        team2: 'Brasil',
        team1Players: jugadoresUruguay,
        team2Players: jugadoresBrasil,
      },
      // Otros partidos...
    ];

    const foundMatch = matches.find(m => m.id === matchId);
    setMatch(foundMatch);
  }, [matchId]);

  if (!match) {
    return <div>Partido no encontrado</div>;
  }

  return (
    <Box>
      <Typography variant="h4" gutterBottom>Formación del Partido: {match.team1} vs {match.team2}</Typography>
      <Grid container spacing={2}>
        <Grid item xs={12} md={6}>
          <Typography variant="h6">Jugadores de {match.team1}</Typography>
          <Grid container spacing={2}>
            {match.team1Players.map(player => (
              <Grid item xs={12} sm={6} md={4} key={player.POS}>
                <PlayerCard player={player} />
              </Grid>
            ))}
          </Grid>
        </Grid>
        <Grid item xs={12} md={6}>
          <Typography variant="h6">Jugadores de {match.team2}</Typography>
          <Grid container spacing={2}>
            {match.team2Players.map(player => (
              <Grid item xs={12} sm={6} md={4} key={player.POS}>
                <PlayerCard player={player} />
              </Grid>
            ))}
          </Grid>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Match2;
