import React, { useState, useEffect } from 'react';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import TextField from '@mui/material/TextField'; // Asegúrate de que esto esté correctamente importado
import { useNavigate } from 'react-router-dom';
import './estilos/Header.css';

const Header = () => {
  const [anchorEl, setAnchorEl] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUsername = localStorage.getItem('username');
    setIsAuthenticated(!!token);
    if (storedUsername) {
      setUsername(storedUsername);
    }
  }, []);

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    setIsAuthenticated(false);
    setUsername('');
    navigate('/login');
    handleMenuClose();
  };

  return (
    <AppBar position="static" className="app-bar">
      <Toolbar>
        <Typography variant="h6" className="logo">
          <img src="logo.png" alt="Logo" height="30" />
          Mi Portal Deportivo
        </Typography>

        <div className="navbar-menu">
          {isAuthenticated && (
            <Typography variant="body1" style={{ marginRight: '20px' }}>
              Bienvenido, {username}
            </Typography>
          )}
          
          <TextField
            placeholder="Buscar..."
            variant="outlined"
            size="small"
            style={{ marginRight: '20px', backgroundColor: 'white', borderRadius: '4px' }}
          />
          
          <IconButton edge="end" color="inherit" aria-label="menu" onClick={handleMenuOpen}>
            <MenuIcon />
          </IconButton>
          <Menu
            id="simple-menu"
            anchorEl={anchorEl}
            keepMounted
            open={Boolean(anchorEl)}
            onClose={handleMenuClose}
          >
            <MenuItem onClick={() => { navigate('/home'); handleMenuClose(); }}>Home</MenuItem>
            <MenuItem onClick={() => { navigate('/players'); handleMenuClose(); }}>Jugadores</MenuItem>
            <MenuItem onClick={() => { navigate('/teams'); handleMenuClose(); }}>Equipos</MenuItem>
            <MenuItem onClick={() => { navigate('/results'); handleMenuClose(); }}>Resultados</MenuItem>
            <MenuItem onClick={() => { navigate('/statistics'); handleMenuClose(); }}>Estadísticas</MenuItem>
            <MenuItem onClick={() => { navigate('/register'); handleMenuClose(); }}>Registro</MenuItem>
            {isAuthenticated ? (
              <MenuItem onClick={handleLogout}>Cerrar sesión</MenuItem>
            ) : (
              <MenuItem onClick={() => { navigate('/login'); handleMenuClose(); }}>Iniciar sesión</MenuItem>
            )}
          </Menu>
        </div>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
