import React from 'react';
import { useParams } from 'react-router-dom';
import data from '../data.json';
import '../componentes/estilos/Match.css'; // Estilos para el hover effect

const Match = () => {
  const { matchId } = useParams();
  const match = data.upcomingMatches.find(m => m.id === parseInt(matchId));

  return (
    <div>
      <h2>Formación del Partido: {match.team1} vs {match.team2}</h2>
      <div className="formation">
        <div className="team team1">
          <h3>{match.team1}</h3>
          {/* Ejemplo de jugadores con efecto hover */}
          {data.players[match.team1].map(player => (
            <div className="player" key={player.POS}>
              <img src={player.Foto} alt={player.Nombre} className="player-image" />
              <div className="player-name">{player.Nombre}</div>
            </div>
          ))}
        </div>
        <div className="team team2">
          <h3>{match.team2}</h3>
          {/* Ejemplo de jugadores con efecto hover */}
          {data.players[match.team2].map(player => (
            <div className="player" key={player.POS}>
              <img src={player.Foto} alt={player.Nombre} className="player-image" />
              <div className="player-name">{player.Nombre}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Match;
