import React from 'react';
import { Grid } from '@mui/material';
import { Typography } from '@mui/material';
import data from '../data.json';
import NewsCard from '../componentes/NewsCard';
import MatchCard from '../componentes/MatchCard';

const Home = () => {
  const news = data.news;
  const upcomingMatches = data.upcomingMatches;
  const sportsResults = data.sportsResults;

  return (
    <Grid container spacing={2}>
      <Grid item xs={12}>
        <Typography variant="h4" gutterBottom>Noticias destacadas</Typography>
        <Grid container spacing={2}>
          {news.map(article => (
            <Grid item xs={12} sm={6} md={4} key={article.id}>
              <NewsCard article={article} />
            </Grid>
          ))}
        </Grid>
      </Grid>
      
      <Grid item xs={12}>
        <Typography variant="h4" gutterBottom>Estadsticas</Typography>
        <Grid container spacing={2}>
          {upcomingMatches.map(match => (
            <Grid item xs={12} sm={6} md={4} key={match.id}>
              <MatchCard match={match} />
            </Grid>
          ))}
        </Grid>
      </Grid>
      
      <Grid item xs={12}>
        <Typography variant="h4" gutterBottom>Resultados de la Copa América</Typography>
        <Grid container spacing={2}>
          {sportsResults.map((result, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <div>
                <a href={result.link} target="_blank" rel="noopener noreferrer">
                  <Typography variant="h6">{result.title}</Typography>
                </a>
                <Typography variant="body2">{result.snippet}</Typography>
              </div>
            </Grid>
          ))}
        </Grid>
      </Grid>
    </Grid>
  );
};

export default Home;
