// CalendarioPartidos.jsx
import React from 'react';
import './CalendarioPartidos.css'; // Archivo de estilos

const CalendarioPartidos = ({ schedule }) => {
  return (
    <div className="calendario-partidos">
      <h2>Calendario de Partidos</h2>
      {schedule.map((day, index) => (
        <div key={index} className="day-schedule">
          <h3>{day.date}</h3>
          {day.matches.map((match, idx) => (
            <div key={idx} className="match">
              <div className="match-info">
                <span>{match.group}</span>
                <span>{match.team1} vs {match.team2}</span>
                <span>{match.status}</span>
              </div>
              <div className="match-score">
                <span>{match.score1} - {match.score2}</span>
                <button>Ver detalles</button>
              </div>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default CalendarioPartidos;
