import React from 'react';
import { useParams } from 'react-router-dom';
import data from '../data.json';
import { jugadoress } from '../jugadores'; // Ruta correcta desde la raíz
import '../componentes/estilos/Match.css'; // Ruta corregida para el archivo CSS

const Match = () => {
  const { matchId } = useParams();
  const match = data.upcomingMatches.find(m => m.id === parseInt(matchId));

  if (!match) {
    return <div>Partido no encontrado</div>;
  }

  const team1Players = jugadoress.filter(player => player.Nacionalidad === match.team1);
  const team2Players = jugadoress.filter(player => player.Nacionalidad === match.team2);

  return (
    <div>
      <h2>Formación del Partido: {match.team1} vs {match.team2}</h2>
      <div className="formation">
        <div className="team team1">
          <h3>{match.team1}</h3>
          {team1Players.length > 0 ? team1Players.map(player => (
            <div className="player" key={player.POS}>
              <img src={player.Foto} alt={player.Nombre} className="player-image" />
              <div className="player-name">{player.Nombre}</div>
            </div>
          )) : <p>No hay datos disponibles para el equipo {match.team1}</p>}
        </div>
        <div className="team team2">
          <h3>{match.team2}</h3>
          {team2Players.length > 0 ? team2Players.map(player => (
            <div className="player" key={player.POS}>
              <img src={player.Foto} alt={player.Nombre} className="player-image" />
              <div className="player-name">{player.Nombre}</div>
            </div>
          )) : <p>No hay datos disponibles para el equipo {match.team2}</p>}
        </div>
      </div>
    </div>
  );
};

export default Match;
