import React from 'react';
import { Grid, Typography } from '@mui/material';
import { Link } from 'react-router-dom'; 
import data from '../data.json';
import NewsCard from '../componentes/NewsCard';
import MatchCard from '../componentes/MatchCard';
import 'flag-icons/css/flag-icons.min.css';
import posiciones from "../posiciones.js"
import resultados from "../resultados.js"
import goleadores from "../goleadores.js"


const Home = () => {
  const { news, upcomingMatches } = data;

  return (
    <Grid container spacing={2}>
      {/* Noticias destacadas */}
      <Grid item xs={12}>
        <Typography variant="h4" gutterBottom>Noticias destacadas</Typography>
        <Grid container spacing={2}>
          {news.map(article => (
            <Grid item xs={12} sm={6} md={4} key={article.id}>
              <a href={article.link} target="_blank" rel="noopener noreferrer" style={{ textDecoration: 'none' }}>
                <NewsCard article={article} />
              </a>
            </Grid>
          ))}
        </Grid>
      </Grid>

      {/* Partidos de la Copa América */}
      <Grid item xs={12}>
        <Typography variant="h4" gutterBottom>Partidos de la Copa América</Typography>
        <Grid container spacing={2}>
          {upcomingMatches.map(match => (
            <Grid item xs={12} sm={6} md={4} key={match.id}>
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <span className={`fi fi-${match.team1FlagCode}`} style={{ fontSize: '2em', marginRight: '10px', marginLeft: '10px' }}></span>
                <Link to={`/match/${match.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                  <MatchCard match={match} />
                </Link>
                <span className={`fi fi-${match.team2FlagCode}`} style={{ fontSize: '2em', marginRight: '10px', marginLeft: '10px' }}></span>
              </div>
            </Grid>
          ))}
        </Grid>
      </Grid>

      {/* Estadísticas */}
      <Grid item xs={12}>
        <Typography variant="h4" gutterBottom>Estadísticas</Typography>
        <Grid container spacing={2}>
          {/* Aquí puedes añadir contenido relacionado con estadísticas */}
          <Grid item xs={12} sm={6} md={4}>
            <Typography variant="h6">Posiciones</Typography>
            <Typography variant="body2">Tabla de posiciones de la Copa America</Typography>
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <Typography variant="h6">Goleadores</Typography>
            <Typography variant="body2">Tabla de goleadores.</Typography>
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <Typography variant="h6">Resultados</Typography>
            <Typography variant="body2">Resultados de todas las fechas.</Typography>
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  );
};

export default Home;
